mapper.select:
select

from sys_role


		and name like concat(#{name}, '%')


		and enable = #{enable}


		and create_time >= #{startTime}


		and create_time <= #{endTime}


order by sort, role_id desc

mapper.delete:
delete
from sys_role where role_id in

	#{item}

mapper.update:
update sys_role
set enable = #{enable}
where role_id = #{roleId}
